        accounts[_owner] = newAA;

        return newAA;
    }

    function getDeployedAA() public view returns (address[] memory) {
        return deployedAA;
    }

    function getAAForVaultId(bytes32 _owner) external view returns (address) {
        return accounts[_owner];
    }
